import{_ as a,c as s,o as t,af as e}from"./chunks/framework.CpesNEOu.js";const p=JSON.parse('{"title":"This page will cover on how to install multiple emulators on multiple distros","description":"","frontmatter":{},"headers":[],"relativePath":"miscellaneous/emulation.md","filePath":"miscellaneous/emulation.md","lastUpdated":1764358901000}'),i={name:"miscellaneous/emulation.md"};function o(n,l,u,r,c,d){return t(),s("div",null,[...l[0]||(l[0]=[e('<h1 id="this-page-will-cover-on-how-to-install-multiple-emulators-on-multiple-distros" tabindex="-1">This page will cover on how to install multiple emulators on multiple distros <a class="header-anchor" href="#this-page-will-cover-on-how-to-install-multiple-emulators-on-multiple-distros" aria-label="Permalink to “This page will cover on how to install multiple emulators on multiple distros”">​</a></h1><h2 id="_1-arch-based-distros" tabindex="-1">1 - Arch based distros <a class="header-anchor" href="#_1-arch-based-distros" aria-label="Permalink to “1 - Arch based distros”">​</a></h2><ul><li><strong>NOTE: I will be using yay mostly because pacman may not have all packages.</strong></li></ul><details class="details custom-block"><summary>Retroarch</summary><ul><li>Install these 3 packages using the command provided below:</li><li><code>yay -S retroarch-assets-xmb --noconfirm</code></li><li><code>yay -S retroarch-assets-ozone --noconfirm</code></li><li><code>sudo pacman -S retroarch --noconfirm</code></li></ul></details><details class="details custom-block"><summary>PCSX2</summary><ul><li>Just install by running <code>yay -S pcsx2 --noconfirm</code></li></ul></details><details class="details custom-block"><summary>Dolphin</summary><ul><li>Just install by running <code>yay -S dolphin-emu --noconfirm</code></li></ul></details><details class="details custom-block"><summary>Rpcs3</summary><ul><li><p>Just install by running <code>yay -S rpcs3-bin --noconfirm</code></p></li><li><p>Also install <em>rpcs3-udev</em> with <code>yay -S rpcs3-udev --noconfirm</code></p></li><li><p><em>rpcs3-udev</em> is need for the emulator to be able to utilize ps4 and ps3 controllers.</p></li></ul></details><details class="details custom-block"><summary>PPSSPP</summary><ul><li>Just install by running <code>yay -S ppsspp --noconfirm</code></li></ul></details><details class="details custom-block"><summary>Duckstation</summary><ul><li>Just install by running <code>yay -S duckstation-gpl --noconfirm</code></li></ul></details><h2 id="_2-debian-based-distros" tabindex="-1">2 - Debian based distros <a class="header-anchor" href="#_2-debian-based-distros" aria-label="Permalink to “2 - Debian based distros”">​</a></h2><ul><li><strong>NOTE: You will need to run <code>sudo apt install flatpak</code> and <code>flatpak remote-add --if-not-exists flathub https://flathub.org/repo/flathub.flatpakrepo</code> before installing most of these.</strong></li></ul><details class="details custom-block"><summary>Retroarch</summary><ul><li>Just install by running <code>sudo apt install retroarch -y</code></li></ul></details><details class="details custom-block"><summary>PCSX2</summary><ul><li><p>Just install by running <code>flatpak install flathub net.pcsx2.PCSX2</code></p></li><li><p>You can run it by using <code>flatpak run net.pcsx2.PCSX2</code></p></li></ul></details><details class="details custom-block"><summary>Dolphin</summary><ul><li><p>Just install by running <code>flatpak install flathub org.DolphinEmu.dolphin-emu</code></p></li><li><p>You can run it by using <code>flatpak run org.DolphinEmu.dolphin-emu</code></p></li></ul></details><details class="details custom-block"><summary>Rpcs3</summary><ul><li><p>Just install by running <code>flatpak install flathub net.rpcs3.RPCS3</code></p></li><li><p>You can run it by using <code>flatpak run net.rpcs3.RPCS3</code></p></li></ul></details><details class="details custom-block"><summary>PPSSPP</summary><ul><li><p>Just install by running <code>flatpak install flathub org.ppsspp.PPSSPP</code></p></li><li><p>You can run it by using <code>flatpak run org.ppsspp.PPSSPP</code></p></li></ul></details><details class="details custom-block"><summary>Duckstation</summary><ul><li><p>Download the latest DuckStation-x64.AppImage from <a href="https://github.com/stenzek/duckstation/releases" target="_blank" rel="noreferrer">https://github.com/stenzek/duckstation/releases</a></p></li><li><p>Run <code>chmod +x DuckStation-x64.AppImage</code> in the directory in which your DuckStation-x64.AppImage file is located in</p></li><li><p>Run <code>./DuckStation-x64.AppImage</code> to run DuckStation</p></li></ul></details>',17)])])}const h=a(i,[["render",o]]);export{p as __pageData,h as default};
